import { createContext, useEffect, useState } from "react";


export const LoggedInCheck = createContext();

export const LoggedInCheckProvider = ({children}) =>{
    const [loginSelected, setLoginSelected] = useState(false);


    return(
    <LoggedInCheck.Provider value={{loginSelected, setLoginSelected}}>
        {children}
    </LoggedInCheck.Provider>
    );
}
